let menuIcon=document.querySelector('.menu-icon');let closeMenuIcon=document.querySelector('.close-icon');let navLinks=document.querySelector('.main-nav-links');let link=document.querySelectorAll('.nav-link');$(window).on('load resize',function(){var windowWidth=window.innerWidth;toggleNavIcon(windowWidth)})
function toggleNavIcon(windowWidth){if(windowWidth<768){menuIcon.classList.remove('present');navLinks.style.display="none"}else{closeMenuIcon.classList.add('present');menuIcon.classList.add('present');navLinks.style.display="block";[].forEach.call(link,function(element){element.style.display="inline-block"})}}
menuIcon.addEventListener('click',function(){menuIcon.classList.add('present');closeMenuIcon.classList.remove('present');navLinks.style.display="block";[].forEach.call(link,function(navLink){navLink.style.display="block"})})
closeMenuIcon.addEventListener('click',function(){closeMenuIcon.classList.add('present');menuIcon.classList.remove('present');navLinks.style.display="none"})